export default function calcCart(){document.querySelectorAll(".cart_popup__table").forEach(t=>{const e=t.querySelectorAll(".cart_item"),a=t.dataset.currency;let r=0,l=0,c=0;e.forEach(t=>{const e=t.querySelector(".cart_item__length_value"),r=Number(e.dataset.price.replaceAll(" ",""))*Number(e.value);if(e.dataset.priceWithoutDiscount){const t=e.dataset.priceWithoutDiscount.replaceAll(" ","");l+=Number(t)*Number(e.value),c+=Number(t)*Number(e.value)-r}else l+=r;t.querySelector("[data-cart-current-price]").textContent=`${r.toLocaleString()} ${a}`}),r=l-c,t.querySelectorAll("[data-cart-sum]").forEach(t=>{t.textContent=`${l.toLocaleString()} ${a}`}),t.querySelectorAll("[data-cart-discount]").forEach(t=>{t.textContent=`${c.toLocaleString()} ${a}`}),t.querySelectorAll("[data-cart-total]").forEach(t=>{t.textContent=`${r.toLocaleString()} ${a}`}),t.querySelectorAll("[data-cart-products-length").forEach(t=>{t.textContent=e.length+" "+function(t,e,a,r){let l=Math.abs(t);return l%=100,l>=5&&l<=20?r:(l%=10,1===l?e:l>=2&&l<=4?a:r)}(e.length,t.dataset.one,t.dataset.two,t.dataset.five)}),e.length?t.classList.remove("is-null"):t.classList.add("is-null")})}