export default function filterCheckSearch(){document.querySelectorAll(".filter__check_search").forEach(e=>{const t=e.querySelector("input"),c=e.closest(".filter__check").querySelector(".filter__check_list");let r=[];c.querySelectorAll(".checkbox").forEach(e=>{const t=e.querySelector(".checkbox__text");r.push([t.textContent,e])}),t.addEventListener("input",e=>{const c=function(e,t){e=e.toLowerCase();const c=[];return Array.from(t).forEach(t=>{t[0].toLowerCase().includes(e)&&c.push(t)}),c}(t.value,r);c?(r.map(e=>e[1].classList.add("is-hidden")),c.map(e=>e[1].classList.remove("is-hidden"))):r.map(e=>e[1].classList.remove("is-hidden"))})})}