export default function rangeSlider(e){document.querySelectorAll(".range").forEach(a=>{const t=a.querySelector("[data-is-min]"),l=a.querySelector("[data-is-max]"),u=a.querySelector(".range__element"),r=new window.JSR.JSR({modules:[new window.JSR.ModuleLimit({min:Number(u.dataset.min),max:Number(u.dataset.max)}),new window.JSR.ModuleRail,new window.JSR.ModuleSlider,new window.JSR.ModuleBar],config:{min:Number(u.dataset.min),max:Number(u.dataset.max),step:1,initialValues:[Number(u.dataset.minValue),Number(u.dataset.maxValue)],container:document.querySelector(".range__element div")}});t.addEventListener("input",()=>{t.value=t.value.replace(/[\D]+/g,"")}),l.addEventListener("input",()=>{l.value=l.value.replace(/[\D]+/g,"")}),t.addEventListener("blur",()=>{r.setRealValue(0,Number(t.value.replace(/[\D]+/g,"")))}),l.addEventListener("blur",()=>{r.setRealValue(1,Number(l.value.replace(/[\D]+/g,"")))}),r.onValueChange((function(a){document.querySelectorAll(".filter_checked_list").forEach(e=>{}),0==a.index?t.value=a.real:1==a.index&&(l.value=a.real),e()})),document.querySelectorAll(".filter_reset").forEach(e=>{e.addEventListener("click",()=>{t.value=t.dataset.value,l.value=l.dataset.value,r.setRealValue(0,Number(t.dataset.value)),r.setRealValue(1,Number(l.dataset.value))})})})}